module.exports = function toSJIS (utf8Char) {
  if (!utf8Char || utf8Char === '') return

  for (let i = 0; i < SJIS_UTF8.length; i++) {
    const kanji = SJIS_UTF8[i][1]

    const posIndex = kanji.indexOf(utf8Char)
    if (posIndex >= 0) {
      return SJIS_UTF8[i][0] + posIndex
    }
  }
}
